INSERT INTO `wp_posts` VALUES('72', '1', '2013-08-19 02:14:07', '2013-08-19 02:14:07', '<p style=\"text-align: left;\"> <strong>Maui Wellness Tours &amp; Retreats offers private, group &amp; family yoga/pilates/massage/sound healing/waterfall meditations &amp; more </strong><strong>as an option on your Road To Hana adventure.  </strong><strong>Located just one mile from Twin Falls at Ananda Sanctuary, a </strong><strong>Maui country style yoga studio with ocean views, rainforest jungle hikes &amp; nearby waterfalls!  On Maui, health &amp; fitness is a daily part of our lifestyle.  Now, you &amp; your family, children &amp; partner have an option of choosing your own health adventure with Maui Wellness Tours!  </strong><span class=\"Apple-style-span\" style=\"display: inline; float: none;\"><span class=\"Apple-style-span\" style=\"display: inline; float: none;\"><span class=\"Apple-style-span\" style=\"display: inline; float: none;\"><strong>\r\n</strong></span></span></span></p>\r\n<p style=\"text-align: center;\"><strong>Booking Office:  691 Hana Highway Unit B, Paia, HI 96779 </strong></p>\r\n<p style=\"text-align: center;\"><strong>OR </strong></p>\r\n<p style=\"text-align: center;\"><strong>Book by phone:  808.463.5856</strong></p>\r\n<a href=\"/wp-content/uploads/2013/07/group-photo-1.jpg\"><img class=\"size-full wp-image-27 aligncenter\" alt=\"Maui Yoga @ Ananda Sancturay\" src=\"/wp-content/uploads/2013/07/group-photo-1.jpg\" width=\"640\" height=\"482\" /></a>\r\n<div><i> </i></div>\r\n<div></div>\r\n&nbsp;', '', '', 'inherit', 'open', 'open', '', '26-revision-v1', '', '', '2013-08-19 02:14:07', '2013-08-19 02:14:07', '', '26', '/2013/08/19/26-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES('73', '1', '2013-08-19 02:20:31', '2013-08-19 12:20:31', '<a href=\"/wp-content/uploads/2013/10/Maui_Wellness_Retreat_Massage61.jpg\"><img class=\"alignnone  wp-image-280\" alt=\"Maui_Wellness_Retreat_Massage6\" src=\"/wp-content/uploads/2013/10/Maui_Wellness_Retreat_Massage61.jpg\" width=\"448\" height=\"283\" /></a>\r\n\r\n&nbsp;\r\n\r\nThis custom relaxation massage treatment uses various massage techniques to fully relax your mind and body. Your experience begins as we play soft ocean sounds and begin defusing a deeply relaxing aroma in the room using the finest essential oils (you will have many scents to choose from).\r\n\r\nNext comes a slow, gradually deepening full body massage using warm organic oils. Your therapist will use a variety of special techniques during this treatment including aromatherapy, Lomi Lomi, Swedish techniques, Deep Tissue and much more.\r\n\r\nThis is the ultimate in relaxation!  Take it slow &amp; enjoy the rest of your vacation...Now your on island time!\r\n\r\n60 minutes - $80\r\n\r\n90 minutes - $110\r\n\r\n120 minutes - $140', 'Maui Bliss', '', 'publish', 'open', 'closed', '', 'aloha-biss-massage', '', '', '2013-10-18 14:59:39', '2013-10-19 00:59:39', '', '0', '/?page_id=73', '0', 'page', '', '0');
