INSERT INTO `wp_posts` VALUES('76', '1', '2013-08-19 02:22:16', '2013-08-19 02:22:16', '[frame_left]\r\n\r\n<a href=\"/wp-content/uploads/2013/10/Maui_Wellness_Retreat_Massage12.jpg\"><img class=\"alignnone  wp-image-288\" alt=\"Young couple receiving a hot stone therapy.\" src=\"/wp-content/uploads/2013/10/Maui_Wellness_Retreat_Massage12.jpg\" width=\"594\" height=\"396\" /> [/frame_left]</a>\r\n\r\nCelebrate your Maui vacation love and romance by adding a romantic couple\'s massage to your visit! Enjoy a couple\'s session at Maui Wellness Center &amp; receive complimentary aromatherapy.\r\n\r\nChoose your style of massage:\r\n\r\n- Acupressure\r\n\r\n- Deep Tissue\r\n\r\n- Lomi Lomi\r\n\r\n- Swedish\r\n\r\n*Option for one or both partners to experience the Sound Healing Table (basically a massage table with speakers built into it playing customized music that allows your body to experience sound in a fully new way! Profoundly relaxing!).\r\n\r\n60 minutes - $80 (each)\r\n\r\n90 minutes - $110 (each)\r\n\r\n120 minutes - $140 (each)\r\n\r\n*Option to add Hot Stones for an additional $10 fee.\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'Couples ', '', 'publish', 'open', 'open', '', 'couples-massage', '', '', '2013-10-18 15:29:54', '2013-10-19 01:29:54', '', '0', '/?page_id=76', '0', 'page', '', '0');
